import logo from "./logo.svg";
import "./App.css";
import React, { useState } from "react";

// Mocked user credentials
const MOCK_USERID = "myusername";
const MOCK_PASSWORD = "mypassword";

function SignInScreen() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLoginClick = () => {
    // Validate the inputs
    if (!username || !password) {
      alert("Please enter a username and password.");
      return;
    }

    if (username !== MOCK_USERID || password !== MOCK_PASSWORD) {
      alert("Invalid username or password.");
      return;
    }

    // Display the confirmation message
    if (window.confirm("Login is authenticated.")) {
      // Redirect to the main screen
      window.location.href = "main.html";
    }
  };

  return (
    <div>
      <h1>Sign In</h1>
      <label htmlFor="username">User Name:</label>
      <input
        type="text"
        id="username"
        name="username"
        value={username}
        onChange={handleUsernameChange}
      />
      <br />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        name="password"
        value={password}
        onChange={handlePasswordChange}
      />
      <br />
      <button onClick={handleLoginClick}>Login</button>
      <br />
      <a href="/forgot-password">Forgot password</a>
    </div>
  );
}

function ForgotPasswordScreen() {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleNewPasswordChange = (event) => {
    setNewPassword(event.target.value);
  };

  const handleResetClick = () => {
    // Validate the inputs
    if (!email || !isValidEmail(email)) {
      alert("Please enter a valid email address.");
      return;
    }

    if (!newPassword || !isStrongPassword(newPassword)) {
      alert("Please enter a strong password.");
      return;
    }

    // Display the success message
    const popup = window.open("", "Popup", "width=200,height=100");
    popup.document.write("<h2>Password was changed!!</h2>");
    popup.document.write("<img src='success-icon.png' alt='Success icon' />");
  };

  return (
    <div>
      <h1>Forgot Password</h1>
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        name="email"
        value={email}
        onChange={handleEmailChange}
      />
      <br />
      <label htmlFor="newPassword">New Password:</label>
      <input
        type="password"
        id="newPassword"
        name="newPassword"
        value={newPassword}
        onChange={handleNewPasswordChange}
      />
      <br />
      <button onClick={handleResetClick}>Change</button>
    </div>
  );
}

// Helper functions
function isValidEmail(email) {
  // Use a regular expression to validate the email address
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}
